/**
 * check if value is an object
 * @example
 * isObject({}); // => true
 * isObject([1, 2, 3]); // => true
 * isObject(Function); // => true
 * isObject(null); // => false
 * @param {*} value
 * @returns {boolean}
 */

export default function isObject(value:  any): boolean{
  const type = typeof value;
  return (value !== null && type === 'object') || type === 'function';
}
